// Format arXiv entries to standard article structure
const input = $input.first().json;
const feed = input.feed || {};
const entries = Array.isArray(feed.entry) ? feed.entry : [feed.entry].filter(Boolean);

const articles = entries.slice(0, 5).map(entry => {
  // Extract authors
  const authors = Array.isArray(entry.author) 
    ? entry.author.map(a => a.name?._).filter(Boolean)
    : entry.author?.name?._ ? [entry.author.name._] : [];

  return {
    title: entry.title?._ || entry.title || 'No title',
    link: entry.id?._ || entry.id || '',
    description: (entry.summary?._ || entry.summary || 'No abstract').replace(/\s+/g, ' ').trim().substring(0, 500),
    source: 'arXiv',
    sourceType: 'research',
    author: authors[0] || 'Unknown',
    pubDate: entry.published?._ || entry.published,
    isoDate: entry.published?._ || entry.published
  };
});

return articles.map(article => ({ json: article }));

// Format DeepMind RSS to standard structure
const items = $input.all();

const articles = items.map(item => {
  const data = item.json;
  return {
    title: data.title || 'No title',
    link: data.link || data.url || '',
    description: (data.contentSnippet || data.description || data.summary || 'No description').substring(0, 500),
    source: 'DeepMind',
    sourceType: 'announcement',
    author: data.creator || data.author || 'Unknown',
    pubDate: data.isoDate || data.pubDate,
    isoDate: data.isoDate || data.pubDate
  };
});

return articles.map(article => ({ json: article }));

// Parse Perplexity response and format to standard structure
const response = $input.first().json;
let newsItems = [];

try {
  // The response should be in response.choices[0].message.content
  const content = response.choices?.[0]?.message?.content || response.content || '';

  // Try to parse JSON from the content
  // Remove markdown code blocks if present
  const cleanContent = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  newsItems = JSON.parse(cleanContent);

  if (!Array.isArray(newsItems)) {
    newsItems = [];
  }
} catch (error) {
  console.log('Perplexity parse error:', error.message);
  newsItems = [];
}

const articles = newsItems.slice(0, 10).map(item => ({
  title: item.title || 'No title',
  link: item.url || '',
  description: item.summary || 'No summary',
  source: item.source || 'Perplexity',
  sourceType: 'news',
  author: item.source || 'Perplexity',
  pubDate: new Date().toISOString(),
  isoDate: new Date().toISOString()
}));

return articles.map(article => ({ json: article }));

