// Enhanced deduplication with URL + title similarity
const items = $input.all();
const articles = items.map(item => item.json);

if (articles.length === 0) {
  return [{ json: { articles: [], count: 0 } }];
}

// Get previously sent articles
const sentArticles = $getWorkflowStaticData('node').sentArticles || {};

// Clean up old entries (30 days)
const thirtyDaysAgo = Date.now() - (30 * 24 * 60 * 60 * 1000);
for (const link in sentArticles) {
  if (sentArticles[link] < thirtyDaysAgo) {
    delete sentArticles[link];
  }
}

// Levenshtein distance for title similarity
function levenshtein(a, b) {
  const matrix = [];
  for (let i = 0; i <= b.length; i++) matrix[i] = [i];
  for (let j = 0; j <= a.length; j++) matrix[0][j] = j;
  for (let i = 1; i <= b.length; i++) {
    for (let j = 1; j <= a.length; j++) {
      if (b.charAt(i - 1) === a.charAt(j - 1)) {
        matrix[i][j] = matrix[i - 1][j - 1];
      } else {
        matrix[i][j] = Math.min(
          matrix[i - 1][j - 1] + 1,
          matrix[i][j - 1] + 1,
          matrix[i - 1][j] + 1
        );
      }
    }
  }
  return matrix[b.length][a.length];
}

function similarity(a, b) {
  const longer = a.length > b.length ? a : b;
  const shorter = a.length > b.length ? b : a;
  if (longer.length === 0) return 1.0;
  return (longer.length - levenshtein(longer, shorter)) / longer.length;
}

// Filter duplicates
const newArticles = [];
const seenTitles = [];

for (const article of articles) {
  const link = article.link || '';
  const title = (article.title || '').toLowerCase();

  // Skip if URL already sent
  if (link && sentArticles[link]) continue;

  // Skip if very similar title already processed
  let isDuplicate = false;
  for (const seenTitle of seenTitles) {
    if (similarity(title, seenTitle) > 0.85) {
      isDuplicate = true;
      break;
    }
  }

  if (!isDuplicate) {
    newArticles.push(article);
    seenTitles.push(title);
  }
}

// Sort by date and source type priority - NEWS FIRST!
const typePriority = { news: 3, announcement: 2, research: 1 };
const sortedArticles = newArticles
  .filter(a => a.isoDate || a.pubDate)
  .sort((a, b) => {
    // First by type priority
    const priorityDiff = (typePriority[b.sourceType] || 0) - (typePriority[a.sourceType] || 0);
    if (priorityDiff !== 0) return priorityDiff;

    // Then by date
    const dateA = new Date(a.isoDate || a.pubDate);
    const dateB = new Date(b.isoDate || b.pubDate);
    return dateB - dateA;
  })
  .slice(0, 25);

// Format for AI
const formattedArticles = sortedArticles.map((article, index) => ({
  index: index + 1,
  title: article.title || 'No title',
  link: article.link || '',
  description: article.description || 'No description',
  source: article.source || 'Unknown',
  sourceType: article.sourceType || 'news',
  date: article.isoDate || article.pubDate || ''
}));

// Mark as sent
const now = Date.now();
formattedArticles.forEach(article => {
  if (article.link) {
    sentArticles[article.link] = now;
  }
});

$getWorkflowStaticData('node').sentArticles = sentArticles;

return [{ json: { articles: formattedArticles, count: formattedArticles.length } }];

